mapper.select:
           select mno,nickname,realname,tel,email,money,photo,zfb,wx,bankcard,regDate,status   from(
    select (@id:= @id + 1) as id,mno,nickname,realname,tel,email,money,photo,zfb,wx,bankcard,regDate,status  from (select @id := 0) b,
(select mno,nickname,realname,tel,email,money,photo,zfb,wx,bankcard,regDate,status from memberInfo
) tab) tab1
            where (id between #{mno } and #{status }) 

        and tab1.nickName =#{nickName}
